import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import BootstrapTable from 'react-bootstrap-table-next';

class App extends Component {
  state = {users: []}
  // state1 = {
  //   products: [
  //     {
  //       id: 1,
  //       name: 'TV',
  //       'price': 1000
  //     },
  //     {
  //       id: 2,
  //       name: 'Mobile',
  //       'price': 500
  //     },
  //     {
  //       id: 3,
  //       name: 'Book',
  //       'price': 20
  //     },
  //   ],
  //   columns: [{
  //     dataField: 'id',
  //     text: 'Product ID'
  //   },
  //   {
  //     dataField: 'name',
  //     text: 'Product Name'
  //   }, {
  //     dataField: 'price',
  //     text: 'Product Price',
  //     sort: true
  //   }]
  // }

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(users => this.setState({ users }));
  }

  render() {
    return (
      <div className="App">
        <BootstrapTable
        striped
        hover
        keyField='id'
        data={ this.state.products }
        columns={ this.state.columns } />

        <h1>Users</h1>
        {this.state.users.map(user =>
          <div key={user.id}>{user.username}</div>
        )}
      </div>
    );
  }
}

export default App;
